mapper.select:
select page_sysuser2.* from ( select page_sysuser1.*,rownum page_num from (
select * from ( select sysuser.id, sysuser.userid, sysuser.username, sysuser.groupid, sysuser.sysid, sysuser.userstate, decode(sysuser.groupid, '1',(select mc from userjd where id=sysuser.sysid), '2',(select mc from userjd where id=sysuser.sysid), '3',(select mc from useryy where id=sysuser.sysid), '4',(select mc from usergys where id=sysuser.sysid) ) sysmc from sysuser )sysuser
)page_sysuser1
where rownum <= ${pageQuery.PageQuery_end}
)page_sysuser2
where page_sysuser2.page_num >= ${pageQuery.PageQuery_start}
